document.addEventListener("DOMContentLoaded",function(){let t=document.querySelector(".inicio__carousel__container");var e=document.querySelector(".inicio__carousel__btn--prev"),n=document.querySelector(".inicio__carousel__btn--next");let c=document.querySelectorAll(".inicio__carousel__item"),i=0;function o(){var e=c[0].clientWidth;t.style.transform=`translateX(${-i*e}px)`}n.addEventListener("click",function(){i<c.length-1&&(i++,o())}),e.addEventListener("click",function(){0<i&&(i--,o())})});